    public int enviarProjeto(Projeto projeto) {
        if (!verificaDadosEmpreendedores(projeto)) {
            return FALTANDO_DADOS;
        }

        projeto.setStatus(Projeto.EM_AVALIACAO);
        if (projetoDao.update(projeto)) {
            return ENVIADO;
        } else {
            return ERRO_AO_SALVAR;
        }
    }

